INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'cbef7beb-acd2-415b-b0fd-41f4cf061d25', N'آیین‌نامه نحوه استفاده از زمین و منابع ملی در مناطق آزاد تجاری ـ صنعتی جمهوری اسلامی ایران', N'16448/ت253ک', CAST(N'1994-04-30T00:00:00.0000000' AS DateTime2), N'14323', CAST(N'1994-05-09T00:00:00.0000000' AS DateTime2), N'فصل سوم - سایر مقررات
ماده ۱۵- کلیه اختیاراتی که در رابطه با تجاوز و تصرف و تخریب اراضی دولتی و ملی و اراضی ساحلی و حریم آن بر اساس قانون حفاظت و بهره‌برداری از جنگل‌ها و مراتع کشور - مصوب سال ۱۳۴۸ و اصلاحات بعدی آن و قانون اراضی مستحدث و ساحلی - مصوب ۱۳۵۴ - به وزارتخانه‌های مسؤل محول شده و همچنین اختیارات موضوع مواد (۱۱) تا (۱۵) قانون حفاظت و بهسازی محیط‌زیست - مصوب ۱۳۵۳ - که به سازمان حفاظت محیط‌زیست تفویض شده است و نیز اختیارات خاصی که در این مورد به سایر نهادهای دولتی محول گردیده است، از تاریخ تصویب این آیین‌نامه در محدوده مناطق آزاد به سازمان تقویض می‌شود.', CAST(N'1994-03-14T00:00:00.0000000' AS DateTime2), 10, 1, N'42d7aea2-25e7-4685-8d25-aa8771494899.pdf', N'51643fd4-aa1b-4bf5-b4c8-75811c1129ef', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'dd966521-9dbe-4050-8b67-200e4517b715', N'246ea6ce-bdf9-4418-af42-d48314d612ff', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.1860015' AS DateTime2), 0)
