INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'23e42e8c-cd01-4b25-9ba4-4254c22fb14d', N'سیاست‌های محرک عرضه زمین و جلوگیری از احتکار اراضی مسکونی در محدوده شهرها ', N'300/ 3705   ', CAST(N'2023-05-29T00:00:00.0000000' AS DateTime2), N'22806', CAST(N'2023-07-11T00:00:00.0000000' AS DateTime2), N'شورای عالی شهرسازی و معماری ایران در جلسه مورخ 1402/03/01در راستای اجرای حداکثری طرح‌های توسعه شهری تحقق کاربری‌های مورد نیاز شهر و به منظور پیشگیری از رانت حبس ظرفیت‌های مسکونی موجود شهرها و قرارگیری این اراضی در چرخه ساخت وساز، در اجرای تبصره ماده ۱۲ قانون جهش تولید مسکن و مصوبه بازتنظیم نظام پهنه‌بندی تراکمی ساختمانی شهرها، مقرر نمود ادارات کل راه و شهرسازی استان‌ها ضمن بازپس‌گیری آن دسته از اراضی واگذار شده به تعاونی‌ها و سایر اشخاص حقیقی و حقوقی که مطابق مفاد قرارداد احداث نشده است، نسبت به پیگیری تغییرکاربری اراضی مسکونی که در طی دوره طرح، اقدامی در جهت احداث بنا در آنها نشده باشد، به کاربری‌های خدماتی اقدام نمایند.
تبصره ـ باز پس‌گیری اراضی دولتی واگذار شده موضوع این مصوبه در چهارچوب آیین‌نامه اجرائی مصوب هیئت‌وزیران انجام خواهد گرفت و اراضی دولتی بازپس گرفته شده از شمول اقدامات بعدی این مصوبه مستثنی می‌باشد.
مراحل و چگونگی انجام تغییر کاربری به ترتیب زیر است:
ـ شناسایی اراضی دارای کاربری مسکونی که در طی دوره طرح در فرآیند ساخت و ساز قرار نگرفته‌اند به تفکیک نوع واگذاری، مساحت زمین، نوع مالکیت (حقیقی، حقوقی، تعاونی)، تاریخ آخرین نقل و انتقال و تاریخ تغییر کاربری به مسکونی (در صورت تغییر کاربری توسط کمیسیون ماده ۵)،  توسط شهرداری و با تایید دبیرخانه کمیسیون ماده ۵.
ـ اعلام ضرب‌الاجل حداکثر ۶ ماهه پس از شناسایی به مالکان این اراضی برای اقدام به دریافت پروانه و احداث بنا توسط شهرداری.
ـ تعیین میزان کمبود کاربری‌های خدماتی عمومی درمقیاس مناسب، با اولویت تامین خدمات عمومی موردنیاز جمعیت افزایش یافته ناشی ازمصوبه بازتنظیم نظام پهنه‌بندی تراکمی ساختمانی شهر با هماهنگی دستگاه‌های اجرایی ذیربط توسط ادارات کل راه و شهرسازی.
ـ ارائه گزارش اقدامات انجام شده به کمیسیون ماده ۵ و پیشنهاد تغییر کاربری آن دسته از اراضی مسکونی که علیرغم پایان ضرب‌الاجل، نسبت به دریافت پروانه و احداث بنا اقدام نکرده‌اند به کاربری‌های خدمات عمومی مورد نیاز (با اولویت اراضی بزرگ مقیاس)
اقدامات فوق‌الذکر در شهرهایی که طرح جامع یا تفصیلی در مرحله تهیه یا بازنگری می‌باشد، در فرآیند تهیه طرح اعمال خواهد شد.
مسئولیت هماهنگی و نظارت بر حسن اجرای این مصوبه با استانداران بوده و استانداری‌ها مکلفند گزارش دوره‌ای انجام مصوبه را به همراه مشکلات اجرایی و پیشنهادات استان در بازه‌های زمانی ۶ماهه به دبیرخانه شورای عالی ارسال نمایند تا ضمن جمع‌بندی و گزارش عملکرد به شورای عالی، در فرآیند اصلاحات احتمالی مصوبه مورد استفاده قرار گیرد.', CAST(N'2023-05-22T00:00:00.0000000' AS DateTime2), 20, 1, N'be0b548e-4ead-4beb-9cd3-7bc920348139.pdf', N'55f69a1f-ff65-4cf5-bae4-9c3d8b29e609', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'b6deaa70-542f-436f-8925-9a8a2cc85bae', N'8a1deee7-938c-4a44-b05a-af85800c638c', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.2786317' AS DateTime2), 0)
GO
INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'3104f84d-05b5-45d4-bc32-453a2a221a45', N'قانون الحاق موادی به قانون تنظیم بخشی از مقررات مالی دولت (۱)', N'52647', CAST(N'2005-12-03T00:00:00.0000000' AS DateTime2), N'17705', CAST(N'2005-12-05T00:00:00.0000000' AS DateTime2), N'ماده ۵۹ ـ به وزارت جهاد کشاورزی (سازمان امور عشایر) اجازه داده می‌شود  برای اسکان عشایر، اراضی، مستحدثات و تجهیزات آبرسانی و آبیاری مربوط را که به منظور اسکان عشایر با اعتبارات دولتی خریداری یا احداث نموده و در اختیار دارد، با دریافت بیست درصد (۲۰ ٪) قیمت کارشناسی روز طی اقساط پنج ساله به عشایر داوطلب اسکان به طور قطعی واگذار نماید. معادل صد درصد (۱۰۰ ٪) وجوه حاصل به حساب درآمد عمومی کشور (نزد خزانه‌داری کل) واریز می‌شود. معادل رقم واریزی از محل اعتباری که به همین منظور در بودجه‌های سنواتی درج می‌گردد برای اجرای طرح‌های تملک دارایی‌های سرمایه‌ای ساماندهی عشایر اختصاص می یابد.', CAST(N'2005-11-06T00:00:00.0000000' AS DateTime2), 20, 1, N'51.pdf', N'26c375f7-3882-4f65-b33c-d4f229e70cdf', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'363189cf-4b77-47fa-8380-3a4b39b8cf5c', N'16aac0ae-4510-4f79-b670-d80b86ce12e5', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.2385023' AS DateTime2), 0)
GO
INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'73521241-d8fb-433f-b409-48c8fa80b834', N'آیین‌نامه اجرایی قانون ایجاد شهرهای جدید ', N'6892/ت 26822ه ', CAST(N'2003-09-15T00:00:00.0000000' AS DateTime2), N'17069', CAST(N'2003-10-04T00:00:00.0000000' AS DateTime2), N'ماده ۷ ‌ـ‌ از تاریخ تصویب طرح جامع، شهرجدید در زمره شهرهای موضوع ماده (۴) قانون نظام مهندسی و‌کنترل ساختمان ‌ـ‌ مصوب ۱۳۷۴‌- قرار گرفته و هرگونه ساخت و‌ ساز درآن موکول به رعایت ماده (۳۰) قانون یادشده می‌باشد. 
ماده ۲۱‌ـ‌ پس از ایجاد و استقرار شهرداری، سازنده شهر جدید موظف است حداکثر ظرف یک ماه، طی صورتجلسه تحویل و تحول، فضاهای عمومی ( فضای سبز، پارک‌ها، معابر،میادین، گذرگاه‌ها، گورستان و غسال‌خانه ) پیش‌بینی شده در طرح و تأ‌سیسات زیربنایی مربوط را پس از تکمیل و آماده شدن نقشه‌ها و اسناد و مدارک مربوط به پرو‌انه‌ها، پایان¬کارها و گواهی عدم خلاف صادر شده و نقشه‌های مرحله‌بندی ساخت، به صورت بلاعوض به شهرداری تحویل دهد. شهرداری نیز مکلف است پرو‌انه‌ها، پایان کارها و گواهی عدم خلاف صادرشده را مورد قبول و رعایت قرار دهد.', CAST(N'2003-08-06T00:00:00.0000000' AS DateTime2), 10, 1, N'90.pdf', N'51643fd4-aa1b-4bf5-b4c8-75811c1129ef', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'2da0dbcd-c82c-47f5-93fc-7613e65be0da', N'0255edc9-b15a-4331-8a63-2d2bb92df047', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.4669706' AS DateTime2), 0)
GO
INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'65e8026a-fba8-4675-9cad-4ac5f29fbc23', N'قانون تشکیل شوراهای آموزش و پرورش در استان‌ها، شهرستان‌ها و مناطق کشور', N'57092', CAST(N'1994-01-27T00:00:00.0000000' AS DateTime2), N'14253', CAST(N'1994-02-07T00:00:00.0000000' AS DateTime2), N'ماده ۱۸ - کلیه احداث کنندگان شهرهای جدید و شهرک‌های مسکونی در تمام نقاط کشور و در داخل و خارج از حریم قانونی شهرها، اعم از اشخاص حقیقی و حقوقی، مؤسسات دولتی، غیر دولتی، نهادها و کارخانه‌ها مکلفند همزمان با تهیه و آماده سازی زمین یا ساخت واحدهای مسکونی و صنعتی به تناسب تعداد و سطح زیربنای اینگونه واحدها نسبت به احداث و تأمین فضای آموزشی و پرورشی مورد‌نیاز در مقاطع مختلف بر اساس استانداردهای مصوب، به‌طور مستقیم یا با پرداخت هزینه مربوط به سازمان نوسازی و توسعه و تجهیز مدارس، بر طبق نظر شورای شهرستان یا منطقه اقدام نمایند.', CAST(N'1994-01-16T00:00:00.0000000' AS DateTime2), 20, 1, N'063cdcbc-5af6-4828-8fab-1a427538e6bd.pdf', N'26c375f7-3882-4f65-b33c-d4f229e70cdf', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'22947706-f18a-4256-9fb0-c5b047df17e2', N'16aac0ae-4510-4f79-b670-d80b86ce12e5', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.2365646' AS DateTime2), 0)
GO
INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'c8edd805-36fe-4dad-b4e0-4c22da759b0d', N'آیین‌نامه استفاده از اراضی، احداث بنا و تأسیسات در ...', N'31636/ت47097هـ', CAST(N'2012-05-09T00:00:00.0000000' AS DateTime2), N'19606', CAST(N'2012-06-27T00:00:00.0000000' AS DateTime2), N'ماده ۴ - ایجاد هرگونه بنا و تأسیسات در خارج از حریم شهرها و محدوده روستاها پس از رعایت مفاد طرح‌های توسعه و عمران (جامع) ناحیه‌ای و مجموعه شهری (در صورت وجود) با رعایت شرایط زیر مجاز است:
الف - حریم قانونی نسبت به راه‌ها و راه‌آهن، معادن، دریا، رودخانه، جنگل‌ها، دریاچه‌ها، تالاب‌ها، نهرهای عمومی، قنوات و چاه‌ها، مسیل‌ها، خطوط و پایه‌های انتقال نیروی برق، خطوط ارتباطی و فیبر نوری و پایه‌های تأسیسات مخابراتی، پل‌ها و تونلهای واقع در مسیر راه‌ها یا تأسیسات عمومی و عمرانی، لوله های انتقال نفت و گاز، سدها و کانالها و شبکه‌های آبیاری، خطوط و لوله های آبرسانی، تأسیسات نظامی و انتظامی و امنیتی و مرزی، تصفیه خانه‌های آب و فاضلاب، سیل بندها، سیل گیرها، ابنیه و آثار تاریخی و طبیعی و تأسیسات هسته‌ای.
ج - قانون حفظ کاربری اراضی زراعی و باغ‌ها - مصوب ۱۳۷۴ - و اصلاحیه‌های بعدی آن.
هـ - ضوابط و مقررات سازمان حفاظت محیطزیست در مناطق چهارگانه تحت مدیریت و مقررات و ضوابط استقرار واحدهای تولیدی، صنعتی و معدنی موضوع ابلاغیه شماره ۲۶۸۶۹/۱۰۰/۹۷ مورخ 1397/07/09 سازمان حفاظت محیطزیست. 
و - آیین‌نامه نصاب اراضی قابل واگذاری به فعالیت‌های مختلف در خارج از حریم شهرها و محدوده روستاها موضوع تصویب‌نامه شماره ۷۹۲۵۹/ت۴۲۸۴۹هـ مورخ 1390/04/15.
تبصره ۱ - تعیین کاربری و ضوابط ساخت و ساز برای هر یک از انواع کاربری‌ها و فعالیت‌ها اعم از عرض گذر، نحوه استقرار بنا، سطح اشغال و تراکم ساختمانی باید به تصویب شورای برنامه‌ریزی و توسعه استان برسد.', CAST(N'2012-04-29T00:00:00.0000000' AS DateTime2), 10, 1, N'b11b264e-201f-432b-9585-3d13ce94e50a.pdf', N'51643fd4-aa1b-4bf5-b4c8-75811c1129ef', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'2da0dbcd-c82c-47f5-93fc-7613e65be0da', N'0255edc9-b15a-4331-8a63-2d2bb92df047', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.4667574' AS DateTime2), 0)
GO
INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'44b641f0-39b0-4f49-98f7-4db61ff382f3', N'آیین‌نامه اجرایی تبصره (۲) اصلاحی ماده (۹) قانون افزایش بهره‌وری بخش کشاورزی و منابع طبیعی موضوع ...  ', N'48287/ت52082هـ ', CAST(N'2015-07-09T00:00:00.0000000' AS DateTime2), N'20492', CAST(N'2015-07-14T00:00:00.0000000' AS DateTime2), N'ماده ۵ - تغییر کاربری اراضی واگذار شده موضوع این آیین نامه ممنوع است و تفکیک اراضی مزبور صرفاً با اخذ مجوز کمیسیون‌های طرح های کشاورزی و غیرکشاورزی موضوع مواد (31)  و (۳۲) آیین نامه اجرایی لایحه قانونی اصلاح لایحه قانونی واگذاری و احیاء اراضی در حکومت جمهوری اسلامی ایران امکان¬¬پذیر بوده و ادارات ثبت اسناد و املاک و دفاتر اسناد رسمی و سایر نهادها و مراجع مربوط مکلفند در موارد تفکیک یا افراز و تقسیم اراضی واگذار شده و تغییر کاربری آنها از سازمان جهاد کشاورزی استعلام نموده و نظر سازمان مذکور را اعمال نماید و چنانچه به طور غیرمجاز و بدون اخذ مجوز از کمیسیون‌های فوق¬الاشاره صورت پذیرد تخلف بوده و سازمان جهاد کشاورزی موظف است فسخ واگذاری و یا استرداد زمین را به ادارات ثبت محل یا دفاتر اسناد رسمی تنظیم¬کننده سند ابلاغ نماید.
تبصره (اصلاحی 1395/11/24) - اراضی واگذار شده به شهرک‌های صنعتی و شهرک‌های کشاورزی دارای کاربری صنعتی و کشاورزی بوده و تفکیک آن با رعایت قوانین و مقررات مربوط از رعایت حکم این ماده مستثنی است.', CAST(N'2015-06-28T00:00:00.0000000' AS DateTime2), 20, 1, N'6807730f-c80f-4e6c-a619-53c931596301.pdf', N'698a0a2e-78b1-4ec8-b140-e8d572969c70', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'363189cf-4b77-47fa-8380-3a4b39b8cf5c', N'0255edc9-b15a-4331-8a63-2d2bb92df047', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.2377486' AS DateTime2), 0)
GO
INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'e4b39c14-8309-4845-992f-4e1bbb26ee70', N'آیین‌نامه اجرایی قانون زمین شهری', N'20053/ت179هـ', CAST(N'1992-07-20T00:00:00.0000000' AS DateTime2), N'13808', CAST(N'1992-08-06T00:00:00.0000000' AS DateTime2), N'‌فصل سوم- نحوه خرید و تملک زمین‌ها
‌ماده ۱۱ - برای ملک زمین‌های بایر و دایر از سوی دولت و شهرداری‌ها  و کلیه دستگاه‌هایی که طبق قانون، حق خرید و تملک دارند، رعایت ضوابط زیر ضروری‌ است:
‌الف- دستگاه تملک¬کننده دارای طرح مصوب بوده و اعتبار خرید زمین نیز تأمین شده باشد.
ب- بالاترین مقام اجرایی دستگاه تملک¬کننده نیاز به تملک زمین را تأیید نماید.
ج- وزارت مسکن و شهرسازی عدم امکان تأمین زمین مناسب را از زمین‌های موات و دولت در آن شهر اعلام نموده باشد.
‌د- در شهرهایی که دارای طرح‌های مصوب شهری است، کاربری زمین موردنیاز، منطبق با طرح باشد.
‌ماده ۱۲ - تشخیص نیاز برای تملک زمین بایر یا دایر و میزان آن برای احداث واحدهای مسکونی یا واگذاری زمین جهت احداث مسکن و سایر نیازهای مربوط ‌به آن‌ها و همچنین تطبیق میزان زمین‌های معوض موردنیاز طرح‌های سایر دستگاه‌ها با طرح مربوط در شهرهای مشمول با وزارت مسکن و شهرسازی است که با‌ در نظر گرفتن اراضی موات و دولتی ظرف یک ماه از تاریخ دریافت استعلام، اعلام نظر خواهد کرد.
‌تبصره - تشخیص زمین‌های موردنیاز طرح‌های سایر دستگاه‌ها در شهرهای یاد شده در قانون ۷ با دستگاه مربوط است.
فصل چهارم- نحوه آماده‌سازی و عمران و واگذاری زمین‌ها
‌ماده ۲۳ - دستگاه‌ها، بنیادها، نهادها و اشخاص حقیقی و حقوقی واگذارکننده زمین در صورتی مجاز به واگذاری خواهند بود که اجرای عملیات زیربنایی‌ زمین‌های آن‌ها طبق ضوابط و تأیید دستگاه‌های اجرایی مربوط، قبل از واگذاری انجام شده باشد.
‌تبصره - در صورتی که تعاونی‌های مسکن قادر به انجام آماده‌سازی تمام زمین یا بخشی از آن باشند می‌تواند طبق ضوابط و مقررات وزارت مسکن و شهرسازی‌ اقدام نمایند. بدهی است هنگام تعیین قیمت قطعی زمین، هزینه آماده‌سازی از قیمت قطعی کسر خواهد شد.', CAST(N'1992-06-14T00:00:00.0000000' AS DateTime2), 10, 1, N'3ec88472-45e9-4678-87f9-87196ecaadc9.pdf', N'51643fd4-aa1b-4bf5-b4c8-75811c1129ef', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'22947706-f18a-4256-9fb0-c5b047df17e2', N'0255edc9-b15a-4331-8a63-2d2bb92df047', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.2368389' AS DateTime2), 0)
GO
INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'ae657b26-f870-4f76-8d4d-4f98b9dcd71f', N'سیاستهای محرک عرضه زمین و جلوگیری از احتکار اراضی مسکونی در محدوده شهرها', N'300/37056', CAST(N'2023-05-29T00:00:00.0000000' AS DateTime2), N'22806', CAST(N'2023-07-11T00:00:00.0000000' AS DateTime2), N'شورای عالی شهرسازی و معماری ایران در جلسه مورخ 1402/03/01در راستای اجرای حداکثری طرح‌های توسعه شهری تحقق کاربری‌های مورد نیاز شهر و به منظور پیشگیری از رانت حبس ظرفیت‌های مسکونی موجود شهرها و قرارگیری این اراضی در چرخه ساخت وساز، در اجرای تبصره ماده ۱۲ قانون جهش تولید مسکن و مصوبه بازتنظیم نظام پهنه‌بندی تراکمی ساختمانی شهرها، مقرر نمود ادارات کل راه و شهرسازی استان‌ها ضمن بازپس‌گیری آن دسته از اراضی واگذار شده به تعاونی‌ها و سایر اشخاص حقیقی و حقوقی که مطابق مفاد قرارداد احداث نشده است، نسبت به پیگیری تغییرکاربری اراضی مسکونی که در طی دوره طرح، اقدامی در جهت احداث بنا در آنها نشده باشد، به کاربری‌های خدماتی اقدام نمایند.
تبصره ـ باز پس‌گیری اراضی دولتی واگذار شده موضوع این مصوبه در چهارچوب آیین‌نامه اجرائی مصوب هیئت‌وزیران انجام خواهد گرفت و اراضی دولتی بازپس گرفته شده از شمول اقدامات بعدی این مصوبه مستثنی می‌باشد.
مراحل و چگونگی انجام تغییر کاربری به ترتیب زیر است:
ـ شناسایی اراضی دارای کاربری مسکونی که در طی دوره طرح در فرآیند ساخت و ساز قرار نگرفته‌اند به تفکیک نوع واگذاری، مساحت زمین، نوع مالکیت (حقیقی، حقوقی، تعاونی)، تاریخ آخرین نقل و انتقال و تاریخ تغییر کاربری به مسکونی (در صورت تغییر کاربری توسط کمیسیون ماده ۵)،  توسط شهرداری و با تایید دبیرخانه کمیسیون ماده ۵.
ـ اعلام ضرب‌الاجل حداکثر ۶ ماهه پس از شناسایی به مالکان این اراضی برای اقدام به دریافت پروانه و احداث بنا توسط شهرداری.
ـ تعیین میزان کمبود کاربری‌های خدماتی عمومی درمقیاس مناسب، با اولویت تامین خدمات عمومی موردنیاز جمعیت افزایش یافته ناشی ازمصوبه بازتنظیم نظام پهنه‌بندی تراکمی ساختمانی شهر با هماهنگی دستگاه‌های اجرایی ذیربط توسط ادارات کل راه و شهرسازی.
ـ ارائه گزارش اقدامات انجام شده به کمیسیون ماده ۵ و پیشنهاد تغییر کاربری آن دسته از اراضی مسکونی که علیرغم پایان ضرب‌الاجل، نسبت به دریافت پروانه و احداث بنا اقدام نکرده‌اند به کاربری‌های خدمات عمومی مورد نیاز (با اولویت اراضی بزرگ مقیاس)
اقدامات فوق‌الذکر در شهرهایی که طرح جامع یا تفصیلی در مرحله تهیه یا بازنگری می‌باشد، در فرآیند تهیه طرح اعمال خواهد شد.
مسئولیت هماهنگی و نظارت بر حسن اجرای این مصوبه با استانداران بوده و استانداری‌ها مکلفند گزارش دوره‌ای انجام مصوبه را به همراه مشکلات اجرایی و پیشنهادات استان در بازه‌های زمانی ۶ماهه به دبیرخانه شورای عالی ارسال نمایند تا ضمن جمع‌بندی و گزارش عملکرد به شورای عالی، در فرآیند اصلاحات احتمالی مصوبه مورد استفاده قرار گیرد.', CAST(N'2023-05-22T00:00:00.0000000' AS DateTime2), 20, 1, N'f147b379-a506-41c6-9cf8-2ca845f73067.pdf', N'5536c46f-e2f3-43eb-b55e-a8e98c8d1547', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'b6deaa70-542f-436f-8925-9a8a2cc85bae', N'8a1deee7-938c-4a44-b05a-af85800c638c', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.2784248' AS DateTime2), 0)
GO
INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'ca5ad2e1-5db1-4bff-8aaf-50247ff82349', N'ثبت و دریافت شناسه یکتای مجوز بر روی درگاه ملی مجوزهای کشور ', N'87277/ت58115هـ ', CAST(N'2020-10-24T00:00:00.0000000' AS DateTime2), N'22028', CAST(N'2020-11-01T00:00:00.0000000' AS DateTime2), N'۳ - مراجع صادرکننده استعلام مرتبط با حریم‌‌ها و زیرساخت‌‌ها از جمله خطوط انتقال گاز و نفت، منابع آب و خطوط انتقال، دکل فشارقوی، میراث فرهنگی، شهر، حریم‌‌های زیست‌ محیطی، منابع طبیعی و مراتع، صنایع دامپزشکی، امنیتی، فرودگاه‌ها، راه ‌آهن، جاده و تأسیسات اتمی مکلفند به درخواست‌های دریافت شده از طریق درگاه ملی مجوزهای کشور، ظرف پانزده روز کاری به صورت واضح، غیرمشروط و غیرقابل تفسیر پاسخ دهند. عدم پاسخ در مهلت یاد شده، به منزله موافقت با درخواست می‌باشد.
تبصره ۱ - پاسخ ثبت شده به منزله پاسخ قطعی مرجع صادرکننده استعلام می‌باشد و غیرقابل تغییر است.
تبصره ۲ - مراجع موضوع این بند موظفند تا پایان آبان ماه ۱۳۹۹، حریم‌‌های مرتبط را اعلام و در پایگاه اطلاع‌‌رسانی اینترنتی خود اعلام نمایند.', CAST(N'2020-10-18T00:00:00.0000000' AS DateTime2), 20, 1, N'397a6349-7b70-4fc3-83a2-8f61c0308b63.pdf', N'5536c46f-e2f3-43eb-b55e-a8e98c8d1547', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'4dacf41c-905a-4bbf-8a68-10b758a82c5b', N'0255edc9-b15a-4331-8a63-2d2bb92df047', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.1855587' AS DateTime2), 0)
GO
INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'e937b4d2-1b28-4cfe-9537-50cc8b9a14c4', N'آیین‌نامه اجرایی تبصره (۲) اصلاحی ماده (۹) قانون افزایش بهره‌وری بخش کشاورزی و منابع طبیعی ...', N'48287/ت52082هـ ', CAST(N'2015-07-09T00:00:00.0000000' AS DateTime2), N'20492', CAST(N'2015-07-14T00:00:00.0000000' AS DateTime2), N'ماده ۲ - کلیه اراضی طرح های عمرانی قابل واگذاری که بعد از تاریخ لازم‌الاجرا شدن قانون واگذار می گردد با رعایت قوانین و مقررات، مطابق با پروانه بهره‌برداری صادره توسط مراجع ذی‌ربط و در صورت درخواست مجری و تأیید اجرای طرح توسط هیأت نظارت موضوع قانون اصلاح ماده (۳۳) اصلاحی قانون حفاظت و بهره برداری از جنگل‌ها و مراتع کشور و موافقت کمیسیون‌های واگذاری موضوع مواد (۳۱) و (۳۲) آیین نامه اجرایی لایحه قانونی اصلاح لایحه قانونی واگذاری و احیاء اراضی در حکومت جمهوری اسلامی ایران قابل انتقال قطعی می باشد.
ماده ۳ - نظارت بر اجرای طرح های موضوع این آیین نامه به عهده هیأت نظارت قانون اصلاح ماده (۳۳) اصلاحی قانون حفاظت و بهره برداری از جنگل‌ها و مراتع کشور می باشد.', CAST(N'2015-06-28T00:00:00.0000000' AS DateTime2), 20, 1, N'3f7d44f8-c8de-4b61-9ee8-cf3b30f74a73.pdf', N'698a0a2e-78b1-4ec8-b140-e8d572969c70', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'363189cf-4b77-47fa-8380-3a4b39b8cf5c', N'0255edc9-b15a-4331-8a63-2d2bb92df047', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.2385444' AS DateTime2), 0)
