INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'28ee5948-0459-400f-96d9-5222f979d2f0', N'آیین‌نامه احداث، توسعه، بهره‌برداری و مدیریت فرودگاه‌های غیرنظامی ', N'19062/ت47491ه ', CAST(N'2013-04-23T00:00:00.0000000' AS DateTime2), N'19847', CAST(N'2013-04-27T00:00:00.0000000' AS DateTime2), N'ماده ۳۴ – شهرداری‌ها، ادارات کل راه و شهرسازی،‌ سازمان‌های وابسته به وزارت جهاد کشاورزی،‌ شرکت‌های زیرمجموعه وزارت نیرو، شرکت‌های تابع وزارت نفت و شرکت‌های زیرمجموعه شرکت ملی نفت ایران و کلیه سازمان‌ها، نهادها و ارگان هایی که مجوز ساخت و یا بهره¬برداری از اماکن را می دهند، قبل از صدور هرگونه مجوز احداث و بهره¬برداری در حریم و اطراف فرودگاه، ملزم به اخذ استعلام از فرودگاه مربوط (نظامی، مشترک و غیرنظامی) می باشند. عدم پاسخ فرودگاه ظرف سه ماه به عنوان موافقت تلقی می‌شود.
ماده ۳۵ – تغییر کاربری اراضی اطراف فرودگاه توسط شهرداری‌ها، ادارات کل راه و شهرسازی،‌ وزارت جهاد کشاورزی و سایر دستگاه‌های متولی باید با اخذ استعلام از فرودگاه های نظامی و غیرنظامی و مطابق با دستورالعمل‌های مربوط باشد. عدم پاسخ فرودگاه ظرف سه ماه به عنوان موافقت تلقی می‌شود.', CAST(N'2013-04-07T00:00:00.0000000' AS DateTime2), 10, 1, N'116.pdf', N'51643fd4-aa1b-4bf5-b4c8-75811c1129ef', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'363189cf-4b77-47fa-8380-3a4b39b8cf5c', N'0255edc9-b15a-4331-8a63-2d2bb92df047', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.2378318' AS DateTime2), 0)
GO
INSERT [dbo].[Law] ([Id], [Title], [AnnouncementNumber], [AnnouncementDate], [NewspaperNumber], [NewspaperDate], [Description], [ApprovalDate], [Type], [IsOriginal], [Pdf], [ApprovalTypeId], [ApprovalStatusId], [ExecutorManagmentId], [ApprovalAuthorityId], [LawCategoryId], [CreatedAt], [Order]) VALUES (N'ffb6ab73-0a4e-4adf-b82f-528d8521b353', N'آیین‌نامه اجرایی قانون منع فروش و واگذاری اراضی فاقد کاربری مسکونی برای امر مسکن به شرکت‌های تعاونی مسکن و سایر اشخاص حقیقی و حقوقی', N'11483/ت29782ه', CAST(N'2004-05-30T00:00:00.0000000' AS DateTime2), N'17259', CAST(N'2004-06-02T00:00:00.0000000' AS DateTime2), N'ماده ۵ ـ هرگونه واگذاری اراضی در داخل مناطق چهارگانه تحت مدیریت سازمان حفاظت محیط‌زیست ممنوع است.', CAST(N'2004-05-26T00:00:00.0000000' AS DateTime2), 10, 1, N'dd26d353-ea03-4321-8d55-a5913e880703.pdf', N'51643fd4-aa1b-4bf5-b4c8-75811c1129ef', N'7e58bc17-f4c1-4fa7-bbd8-a2c337e5aff1', N'dd966521-9dbe-4050-8b67-200e4517b715', N'0255edc9-b15a-4331-8a63-2d2bb92df047', N'046e0bd3-3d0a-4a25-829a-48ddfdcc65ef', CAST(N'2024-06-08T12:09:52.1859548' AS DateTime2), 0)
